import React from 'react'
import LastYearMovies from '../components/LastYearMovies'
import { useDispatch } from 'react-redux'
import { NavBar } from '../components/NavBar'
import axios from 'axios'
import { setMovies } from '../store/LastYearMoviesReducer'
import { Route, Switch, Redirect, useHistory } from 'react-router-dom';



export const Main = () => {
    const dispatch = useDispatch();
    const history = useHistory()
    let changeValue;

    const handleChange = (e) => {
        changeValue = e.target.value
        return changeValue = changeValue.trim()
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        axios.get(`http://www.omdbapi.com/?s=${changeValue}&apikey=1c36a0b0`)
            .then(response => {
                console.log(response)
                return response.data
            })
            .then(data => {
                dispatch(setMovies(data))

            })
            .catch(error => console.log(error));
    }


    return (
        <div>
            <Switch>
                <Route exact path="/movies" render={() => <NavBar />} />
                <Route exact path="/" render={() => {
                    return (
                        <div>
                            <NavBar handleSubmit={handleSubmit} handleChange={handleChange} />
                            <LastYearMovies />
                        </div>
                    )
                }} />
                <Redirect to="/" />
            </Switch>

        </div>
    )
}

export default Main
