import React from 'react'
import { Button } from 'react-bootstrap'
import { Card } from 'react-bootstrap'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom';


const LastYearMovies = ({ handleClick, handleClickFavourites }) => {
    const movies = useSelector((state) => {
        return state.movies.Search
    })
    const favMovies = useSelector((state) => {
        return state.favouriteMovies
    })
    const searchedUser = useSelector((state) => {
        return state.searchedUser.id
    })

    return (
        <div>
            <Button onClick={() => { console.log(favMovies) }}>favourites</Button>
            {searchedUser ? <Card style={{ width: '18rem' }}>
                {/* <Card.Img variant="top" src={searchedUser.Poster} /> */}
                <Card.Body>
                    <Card.Title>{searchedUser.Title}</Card.Title>
                    <Card.Text>
                        {searchedUser}
                    </Card.Text>
                    <Button variant="primary" onClick={() => { handleClick(searchedUser.imdbID) }} >Favourites</Button>
                </Card.Body>
            </Card> : <h3>Try another search!</h3>}

            {movies ? movies.map((movie) => {
                return (<Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" src={movie.Poster} />
                    <Card.Body>
                        <Card.Title>{movie.Title}</Card.Title>
                        <Card.Text>
                            {movie.Year}
                        </Card.Text>
                        <Link to={`/movie/${movie.imdbID}`}>
                            <Button variant="primary" onClick={() => { handleClick(movie.imdbID) }} >Go somewhere</Button>
                        </Link>
                    </Card.Body>
                </Card>)
            }) : <h3>Try another search!</h3>}

        </div>
    )
}

export default LastYearMovies;
